from nltk.corpus import words
word_list = words.words()

import requests
import json


f = open('npm_data.json', 'w')

for i in word_list[::-1]:
    try:
        x = requests.get('https://www.npmjs.com/package/' + i.lower())
        if x.status_code == 404:
            print(i.lower())
            f.write(i.lower() + '\n')
    except Exception as e:
        print(e)